mapper.select:
select * from employee_info


		org_id=#{orgSearchId}


		and number like concat('%', #{account}, '%')


		and status=#{employeeStatusId}


		and user_name like concat('%', #{username}, '%')

mapper.update:
update
employee_info

set status = #{end}

where id = #{id,jdbcType=INTEGER}

